import React, { Component } from 'react';
import './App.css';

import TaskOwner from './taskOwner/taskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants'

class App extends Component {
  state = {
    task: [
      {fname: 'Ronnie'},
      {lname: ''},
      {task: ''}
    ]
  }

  taskChangedHandler = () => {
    const taskChanged = this.state.task;
    this.setState({
      task: taskChanged
    })
  }

  render () {
    
    return (
      <div className="App">
        <h1>React Task Manager</h1>

        <TaskOwner 
          fname = {this.state.task[0].fname}
          lname = {this.state.task[1].lname}
          task = {this.state.task[2].task}
        />
        <Tasks>
          <label>first Name: </label>
          {this.state.task[0].fname}
        </Tasks>

        <Attendants />
      </div>
    );
  }
}

export default App;
