import React, { Component } from 'react';
import './App.css';

import TaskOwner from './taskOwner/taskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants'

class App extends Component {
  state = {
    task: [
      {id: kdkdk, fname: 'Ronnie'},
      {id: ksijjs, lname: ''},
      {id: yeisk, task: ''}
    ]
  }

  taskChangedHandler = (event, id) => {
    const taskIndex = this.state.task.findIndex(index => {
      return index.id === id;
    })

    const Task = {
      ...this.state.task[taskIndex]
    }

    Task.fname = event.target.value

    const taskChanged = [...this.state.task];
    taskChanged[taskIndex] = Task;
    this.setState({
      task: taskChanged
    })
  }

  render () {
    
    return (
      <div className="App">
        <h1>React Task Manager</h1>

        <TaskOwner 
          fname = {this.state.task[0].fname}
          lname = {this.state.task[1].lname}
          task = {this.state.task[2].task}
          change = {(event) => this.taskChangedHandler (event, this.state.task[0].id)}
        />
        <Tasks>
          <label>first Name: </label>{this.state.task[0].fname}
        </Tasks>

        <Attendants />
      </div>
    );
  }
}

export default App;
