import React, { Component } from 'react';
import './App.css';
import TaskOwner from './TaskOwner/TaskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants';

class App extends Component {
  state = {
    showTask: false
  }

  render () {
    
    return (
      <div className="App">
         <h1>React Task Manager</h1>
        <button>Show TaskOwner</button>
        <button>Attend to Assigned Tasks</button>
       
        <TaskOwner 
          name = 'Ronnie Web'
          task = "hey am a task !"
        />

        <hr />
        <Tasks />

        <hr />
        <Attendants />
      </div>
    );
  }
}

export default App;
