import React, { Component } from 'react';
import './App.css';

import TaskOwner from './taskOwner/taskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants'

class App extends Component {
  state = {
    task: [
      {id: 'kdkdk', fname: 'Ronnie'},
      {id: 'ksijjs', lname: 'Namwanza'},
      {id: 'yeisk', task: 'Hey this is my first task !'}
    ]
  }

  taskChangedHandler = (event, id) => {
    const taskIndex = this.state.task.findIndex(index => {
      return index.id === id;
    })

    const Task = {
      ...this.state.task[taskIndex]
    }

    Task.fname = event.target.value

    const taskChanged = [...this.state.task];
    taskChanged[taskIndex] = Task;
    this.setState({
      task: taskChanged
    })
  }

  render () {
    
    return (
      <div className="App">
        <h1>React Task Manager</h1>

        <TaskOwner 
          fname = {this.state.task[0].fname}
          lname = {this.state.task[1].lname}
          task = {this.state.task[2].task}
          change = {(event) => this.taskChangedHandler (event, this.state.task[0].id)}
        />

        <hr />  
        <Tasks>
          <div style={{width: '60%'}}>
            <label>first Name: </label>{this.state.task[0].fname}
            <label>last Name: </label>{this.state.task[1].lname}
            <label>Task: </label>{this.state.task[2].task}
          </div>

          <label>Full Name</label><input type="text" />
          <label>Your answer</label><textarea  />
          <div>
            <button>Submit</button>
          </div>
        </Tasks>

        <hr />

        <Attendants>
          <ol>
            <li>Henry</li>
            <li>Roger</li>
            <li>Angel</li>
            <li>Ronnie</li>
            <li>Willy</li>
          </ol>
        </Attendants>
      </div>
    );
  }
}

export default App;
