import React, { Component } from 'react';
import './App.css';
import TaskOwner from './TaskOwner/TaskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants';
import Button from '@material-ui/core/Button';
import Styled from 'styled-components';

const StyledButton = Styled.button `
      background-color: &{props => props.alt ? green: red};
      color: white;
      padding: 10px;
      border-style: none;
      border-radius: 5px;
      &:hover {
        background-color: &{props => props.alt ? orange: yellow};
        color: white;
        cursor: pointer
      }
`;

class App extends Component {
  state = {
    showTask: true
  }

  showTaskHandler = () => {
    const displayTask = this.state.showTask;
    this.setState({
      showTask: !displayTask
    })
  }
  render () {
    
    return (
      <div className="App">
         <h1>React Task Manager</h1>

        <Button 
          onClick = {this.showTaskHandler}
          style = {{textTransform: 'capitalize', margin: '10px'}}
          variant = "contained"
          color = "secondary"
        >Show TaskOwner</Button>
        <StyledButton 
          onClick = {this.showTaskHandler}>
          Attend to Assigned Tasks
        </StyledButton>
       
        {
          this.state.showTask ? 
            <TaskOwner 
              name = 'Ronnie Web'
              task = "hey am a task !"
            />
            :
            <div>
              <Tasks />
              <hr />
              <Attendants />
            </div>
        }
      </div>
    );
  }
}

export default App;
