import React, { Component } from 'react';
import './App.css';
import TaskOwner from './TaskOwner/TaskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants';

class App extends Component {
  state = {
    showTask: false
  }

  showTaskHandler = () => {
    const displayTask = this.state.showTask;
    this.setState({
      showTask: !displayTask
    })
  }
  render () {
    
    return (
      <div className="App">
         <h1>React Task Manager</h1>

        <button onClick = {this.showTaskHandler}>Show TaskOwner</button>
        <button onClick = {this.showTaskHandler}>Attend to Assigned Tasks</button>
       
        {
          this.state.showTask ? 
            <TaskOwner 
              name = 'Ronnie Web'
              task = "hey am a task !"
            />
            :
            <div>
              <Tasks />
              <hr />
              <Attendants />
            </div>
        }
      </div>
    );
  }
}

export default App;
