import React, { Component } from 'react';
import './App.css';
import TaskOwner from './TaskOwner/TaskOwner';
import Tasks from './Tasks/Tasks';
import Attendants from './Attendants/Attendants';
import Button from '@material-ui/core/Button';

class App extends Component {
  state = {
    showTask: true
  }

  showTaskHandler = () => {
    const displayTask = this.state.showTask;
    this.setState({
      showTask: !displayTask
    })
  }
  render () {
    
    return (
      <div className="App">
         <h1>React Task Manager</h1>

        <Button 
          onClick = {this.showTaskHandler}
          variant = "contained"
          color = "secondary"
        >Show TaskOwner</Button>
        <button onClick = {this.showTaskHandler}>Attend to Assigned Tasks</button>
       
        {
          this.state.showTask ? 
            <TaskOwner 
              name = 'Ronnie Web'
              task = "hey am a task !"
            />
            :
            <div>
              <Tasks />
              <hr />
              <Attendants />
            </div>
        }
      </div>
    );
  }
}

export default App;
